mapper.sql:
where whorder.dr = 0
and whorder.out_wh_status in (0, 10, 15)
and whorder.out_wh_status in (15, 20)

    and whorder.out_wh_type = #{productType}


    and whorder.out_type = #{outboundOrderType}

mapper.select:
select templete.id as templateId, templete.qc_item_name as itemName from ope_production_quality_tempate templete where templete.dr = 0 and templete.production_id = #{enter.id}
and templete.production_type in (1,2,3)
and templete.production_type =#{produtionType}
