mapper.select:
select count(*) from cor_tenant_scooter scooter left join cor_driver_scooter driverScooter on scooter.scooter_id = driverScooter.scooter_id and driverScooter.dr = 0 and driverScooter.status = 1 left join cor_driver driver on driver.id = driverScooter.driver_id and driver.dr = 0 left join cor_user_profile profile on profile.USER_ID = driver.user_id and profile.dr = 0 where scooter.tenant_id = #{tenantId} and scooter.dr = 0
and scooter.status=#{status}
and ( profile.FIRST_NAME like concat('%',#{keyword},'%') or profile.LAST_NAME like concat('%',#{keyword},'%') or profile.FULL_NAME like concat('%',#{keyword},'%') or scooter.license_plate like concat('%',#{keyword},'%') )

select scooter.scooter_id as id, scooter.status as status, scooter.license_plate as licensePlate, driver.id as driverId, profile.FIRST_NAME as driverFirstName, profile.LAST_NAME as driverLastName from cor_tenant_scooter scooter left join cor_driver_scooter driverScooter on scooter.scooter_id = driverScooter.scooter_id and driverScooter.dr = 0 and driverScooter.status = 1 left join cor_driver driver on driver.id = driverScooter.driver_id and driver.dr = 0 left join cor_user_profile profile on profile.USER_ID = driver.user_id and profile.dr = 0 where scooter.tenant_id = #{tenantId} and scooter.dr = 0
and scooter.status=#{status}
and ( profile.FIRST_NAME like concat('%',#{keyword},'%') or profile.LAST_NAME like concat('%',#{keyword},'%') or profile.FULL_NAME like concat('%',#{keyword},'%') or scooter.license_plate like concat('%',#{keyword},'%') )
order by scooter.status desc limit #{startRow},#{pageSize}

select count(*)
from cor_driver_scooter_history histroy
inner join cor_driver driver on histroy.driver_id = driver.id and driver.dr = 0
inner join cor_user_profile profile on driver.user_id = profile.user_id and profile.dr = 0
where histroy.status = 2
and histroy.scooter_id = #{id}
and histroy.tenant_id = #{tenantId}

    and histroy.begin_time between #{assignStartTime} and #{assignEndTime}


    and histroy.end_time between #{removeStartTime} and #{removeEndTime}


    and (
    profile.first_name like concat('%',#{keyword},'%') or
    profile.last_name like concat('%',#{keyword},'%') or
    profile.full_name like concat('%',#{keyword},'%')
    )

select count(*)
from cor_driver_scooter scooter
inner join cor_driver_scooter_history histroy
on scooter.scooter_id = histroy.scooter_id and scooter.begin_time = histroy.begin_time
inner join cor_driver driver on histroy.driver_id = driver.id and driver.dr = 0
inner join cor_user_profile profile on driver.user_id = profile.user_id and profile.dr = 0
where scooter.status = 1
and histroy.scooter_id = #{id}
and histroy.tenant_id = #{tenantId}

    and histroy.begin_time between #{assignStartTime} and #{assignEndTime}


    and histroy.end_time between #{removeStartTime} and #{removeEndTime}


    and (
    profile.first_name like concat('%',#{keyword},'%') or
    profile.last_name like concat('%',#{keyword},'%') or
    profile.full_name like concat('%',#{keyword},'%')
    )

select histroy.id as id, histroy.scooter_id as scooterId, histroy.begin_time as assignedTime, histroy.end_time as removeTime, histroy.driver_id as driverId, profile.first_name as driverFirstName, profile.last_name as driverLastName, ifnull(histroy.mileage,0) as mileage from cor_driver_scooter_history histroy inner join cor_driver driver on histroy.driver_id = driver.id and driver.dr = 0 inner join cor_user_profile profile on driver.user_id = profile.user_id and profile.dr = 0 where histroy.status = 2 and histroy.scooter_id = #{id} and histroy.tenant_id = #{tenantId}
and histroy.begin_time between #{assignStartTime} and #{assignEndTime}
and histroy.end_time between #{removeStartTime} and #{removeEndTime}
and ( profile.first_name like concat('%',#{keyword},'%') or profile.last_name like concat('%',#{keyword},'%') or profile.full_name like concat('%',#{keyword},'%') )
order by histroy.begin_time desc limit #{startRow},#{pageSize}

select histroy.id as id, histroy.scooter_id as scooterId, histroy.begin_time as assignedTime, histroy.end_time as removeTime, histroy.driver_id as driverId, profile.first_name as driverFirstName, profile.last_name as driverLastName, ifnull(histroy.mileage,0) as mileage from cor_driver_scooter scooter inner join cor_driver_scooter_history histroy on scooter.scooter_id = histroy.scooter_id and scooter.begin_time = histroy.begin_time inner join cor_driver driver on histroy.driver_id = driver.id and driver.dr = 0 inner join cor_user_profile profile on driver.user_id = profile.user_id and profile.dr = 0 where scooter.status = 1 and histroy.scooter_id = #{id} and histroy.tenant_id = #{tenantId}
and histroy.begin_time between #{assignStartTime} and #{assignEndTime}
and histroy.end_time between #{removeStartTime} and #{removeEndTime}
and ( profile.first_name like concat('%',#{keyword},'%') or profile.last_name like concat('%',#{keyword},'%') or profile.full_name like concat('%',#{keyword},'%') )
order by histroy.begin_time desc limit #{startRow},#{pageSize}
