mapper.select:
select count(*)
from cor_express_order a
left join cor_express_delivery_detail b on a.id = b.express_order_id and b.dr=0
left join cor_express_delivery c on b.express_delivery_id = c.id and c.dr=0
left join cor_driver d on c.driver_id = d.id and d.dr=0
left join cor_user_profile e on e.user_id = d.user_id and e.dr=0
where a.tenant_id=#{tenantId}
and a.dr=0

    and a.status=#{status}


    and a.recipient_city=#{city}


    and a.recipient_postcode=#{postalCode}


    and a.created_time between #{creatStartTime} and #{creatEndTime}


    and a.delivered_time between #{deliveredStartTime} and #{deliveredEndTime}


    and(a.order_no like concat('%',#{keyword},'%')
    or a.recipient_name like concat('%',#{keyword},'%')
    or a.recipient_mail like concat('%',#{keyword},'%')
    or e.FULL_NAME like concat('%',#{keyword},'%')
    )

select a.id as id, a.batch_no as batchNo, a.order_no as orderNo, a.status as status, a.recipient_postcode as recipientPostcode, a.recipient_name as recipientName, a.recipient_mail as recipientMail, a.recipient_address as recipientAddress, a.recipient_city as recipientCity, a.created_time as createdTime, a.delivered_time as deliveredTime, e.first_name as driverFirstName, e.last_name as driverLastName from cor_express_order a left join cor_express_delivery_detail b on a.id = b.express_order_id and b.dr=0 left join cor_express_delivery c on b.express_delivery_id = c.id and c.dr=0 left join cor_driver d on c.driver_id = d.id and d.dr=0 left join cor_user_profile e on e.user_id = d.user_id and e.dr=0 where a.tenant_id=#{tenantId} and a.dr=0
and a.status=#{status}
and a.recipient_city=#{city}
and a.recipient_postcode=#{postalCode}
and a.created_time between #{creatStartTime} and #{creatEndTime}
and a.delivered_time between #{deliveredStartTime} and #{deliveredEndTime}
and(a.order_no like concat('%',#{keyword},'%') or a.recipient_name like concat('%',#{keyword},'%') or a.recipient_mail like concat('%',#{keyword},'%') or e.FULL_NAME like concat('%',#{keyword},'%') )
order by a.created_time desc limit #{startRow},#{pageSize}
