mapper.select:
select cdy.id, cdy.order_no as orderNo, cdy.status, '' as label, cdy.recipient, cdy.recipient_address as recipientAddress, cdy.recipient_email as recipientEmail, cdy.recipient_tel as recipientTel, cdy.country_code as countryCode, cdy.parcel_quantity as parcelQuantity, cdy.house_info as houseInfo, cdy.goods_inventory as goodsInventory, cdy.latitude, cdy.longitude, cdy.created_time as createdTime, cdy.ata as deliveredTime, cd.id as driverId, cup.last_name as driverLastName, cup.first_name as driverFirstName, cts.license_plate as licensePlate, cts.scooter_id as scooterId from cor_delivery cdy inner join cor_user_profile cup on cdy.deliverer_id = cup.user_id and cup.dr = 0 inner join cor_driver cd on cup.user_id = cd.user_id and cd.dr = 0 inner join cor_tenant_scooter cts on cdy.scooter_id = cts.scooter_id and cts.dr = 0 where cdy.dr = 0 and cdy.tenant_id=#{tenantId}
and cdy.status =#{status}
and cdy.created_time between #{creatStartDateTime} and #{creatEndDateTime}
and cdy.ata between #{ataStartDateTime} and #{ataEndDateTime}
and ( cdy.order_no like concat("%",#{keyword},"%") or cdy.recipient like concat("%",#{keyword},"%") or cdy.recipient_tel like concat("%",#{keyword},"%") or cup.last_name like concat("%",#{keyword},"%") or cup.first_name like concat("%",#{keyword},"%") or cts.license_plate like concat("%",#{keyword},"%") or cdy.house_info like concat("%",#{keyword},"%") )
group by id, orderNo, status, label, recipient, recipientAddress, recipientEmail, recipientTel, countryCode, parcelQuantity, latitude, longitude, createdTime, deliveredTime, driverId, driverLastName, driverFirstName, licensePlate, scooterId order by deliveredTime, createdTime desc limit #{startRow},#{pageSize}

select count(cdy.id) from cor_delivery cdy inner join cor_user_profile cup on cdy.deliverer_id = cup.user_id and cup.dr = 0 inner join cor_driver cd on cup.user_id = cd.user_id and cd.dr = 0 inner join cor_tenant_scooter cts on cdy.scooter_id = cts.scooter_id and cts.dr = 0 where cdy.dr = 0 and cdy.tenant_id=#{tenantId}
and cdy.status =#{status}
and cdy.created_time between #{creatStartDateTime} and #{creatEndDateTime}
and cdy.ata between #{ataStartDateTime} and #{ataEndDateTime}
and ( cdy.order_no like concat("%",#{keyword},"%") or cdy.recipient like concat("%",#{keyword},"%") or cdy.recipient_tel like concat("%",#{keyword},"%") or cup.last_name like concat("%",#{keyword},"%") or cup.first_name like concat("%",#{keyword},"%") or cts.license_plate like concat("%",#{keyword},"%") or cdy.house_info like concat("%",#{keyword},"%") )
