mapper.select:
select DATE_FORMAT(cdy.updated_time, '%Y-%m-%d %H') as times, ifnull(sum(case when cdy.status = '3' then 1 end), 0) as rejected, ifnull(sum(case when cdy.status = '5' then 1 end), 0) as timeoutComplete, ifnull(sum(case when cdy.status = '6' then 1 end), 0) as completed, ifnull(sum(case when cdy.status = '7' then 1 end), 0) as cancel, ifnull(sum(case when cdy.status = '3' then 1 when cdy.status = '5' then 1 when cdy.status = '6' then 1 when cdy.status = '7' then 1 end), 0) as total from cor_delivery cdy where cdy.dr = 0 and cdy.tenant_id=#{tenantId} and DATE_FORMAT(cdy.updated_time, '%Y-%m-%d') = DATE_FORMAT(#{dateTime}, '%Y-%m-%d')
and cdy.status=#{status}
group by times

select DATE_FORMAT(cdy.updated_time, '%Y-%m-%d') as times, ifnull(sum(case when cdy.status = '3' then 1 end), 0) as rejected, ifnull(sum(case when cdy.status = '5' then 1 end), 0) as timeoutComplete, ifnull(sum(case when cdy.status = '6' then 1 end), 0) as completed, ifnull(sum(case when cdy.status = '7' then 1 end), 0) as cancel, ifnull(sum(case when cdy.status = '3' then 1 when cdy.status = '5' then 1 when cdy.status = '6' then 1 when cdy.status = '7' then 1 end), 0) as total from cor_delivery cdy where cdy.dr = 0 and cdy.tenant_id=#{tenantId} and (DATE_FORMAT(cdy.updated_time, '%Y-%m-%d') between DATE_FORMAT(#{startDateTime}, '%Y-%m-%d') and DATE_FORMAT(#{endDateTime}, '%Y-%m-%d'))
and cdy.status=#{status}
group by times

