mapper.select:
select express.id as id, express.status as status, driver.id as driverId, profile.first_name as driverFirstName, profile.last_name as driverLastName, tscooter.scooter_id as scooterId, tscooter.license_plate as licensePlate, express.order_complete_num as completeCount, express.order_sum as totalCount, express.delivery_date as taskTime, express.delivery_start_time as startTime, express.delivery_end_time as deliveredTime from cor_express_delivery express inner join cor_driver driver on express.driver_id = driver.id and driver.dr = 0 and driver.status = 1 inner join cor_driver_scooter dscooter on dscooter.driver_id = driver.id and dscooter.status = 1 and dscooter.dr = 0 inner join cor_user_profile profile on driver.user_id = profile.user_id and profile.dr = 0 inner join cor_tenant_scooter tscooter on tscooter.scooter_id = dscooter.scooter_id and tscooter.dr=0 where express.tenant_id =#{tenantId} and express.dr = 0
and express.id not in
#(iterm)
and TO_DAYS(express.create_time) = TO_DAYS(NOW())
and TO_DAYS(express.create_time) < TO_DAYS(NOW())
and express.status=#{status}
and express.delivery_date between #{taskStartTime} and #{taskEndTime}
and (express.delivery_start_time between #{startStartTime} and #{startEndTime})
and (express.delivery_end_time between #{deliveredStartTime} and #{deliveredEndTime})
and (profile.first_name like concat('%',#{keyword},'%') or profile.last_name like concat('%',#{keyword},'%') or profile.full_name like concat('%',#{keyword},'%') or tscooter.license_plate like concat('%',#{keyword},'%') ) order by express.create_time desc limit #{startRow},#{pageSize}

select count(*)
from cor_express_order eorder
where eorder.dr = 0
and eorder.tenant_id = #{tenantId}
and eorder.status = 1

    and eorder.recipient_city=#{recipientCity}


    and eorder.recipient_postcode = #{recipientPostcode}


    and (eorder.created_time between #{createdStartTime} and #{createdEndTime})


    and (eorder.expect_time_begin  <=  #{expectTimeStart} and eorder.expect_time_end
     >  #{expectTimeEnd} )

select eorder.id as id, eorder.status as status, eorder.recipient_country as recipientCountry, eorder.recipient_province as recipientProvince, eorder.recipient_city as recipientCity, eorder.recipient_postcode as recipientPostcode, eorder.recipient_address as recipientAddress, eorder.expect_time_begin as expectTimeStart, eorder.expect_time_end as expectTimeEnd from cor_express_order eorder where eorder.dr = 0 and eorder.tenant_id = #{tenantId} and eorder.status = 1
and eorder.recipient_city=#{recipientCity}
and eorder.recipient_postcode = #{recipientPostcode}
and (eorder.created_time between #{createdStartTime} and #{createdEndTime})
and (eorder.expect_time_begin <= #{expectTimeStart} and eorder.expect_time_end > #{expectTimeEnd} )
order by eorder.created_time desc limit #{startRow},#{pageSize}

select * from cor_express_order where dr=0
and id in
#{item}
