mapper.select:
select count(cdr.id) from cor_driver cdr inner join cor_user_profile cup on cdr.user_id = cup.user_id and cup.dr = 0 inner join cor_driver_scooter cds on cdr.id = cds.driver_id and cds.dr = 0 left join cor_tenant_scooter cts on cds.scooter_id = cts.scooter_id and cts.dr = 0
cdr.dr=0 and cdr.tenant_id = #{tenantId}
and ( cup.first_name like concat("%",#{keyword},"%") or cup.last_name like concat("%",#{keyword},"%") or cup.email_1 like concat("%",#{keyword},"%") or cup.tel_number_1 like concat("%",#{keyword},"%") )
and ( cup.first_name like concat("%",#{keyword},"%") or cup.last_name like concat("%",#{keyword},"%") )
and cdr.status = #{status}

select count(*)
 from cor_delivery_trace trace
          inner join cor_driver cd on trace.user_id = cd.user_id and cd.dr = 0
          inner join cor_delivery delivery on trace.delivery_id = delivery.id and delivery.dr = 0
 where trace.tenant_id = #{tenantId}
   and trace.dr = 0
   and cd.id = #{id}
   and trace.status in (
                        '3', '5', '6', '7'
     )

select
   count(*)
   from cor_driver_scooter_history cdsh
   inner join cor_tenant_scooter cts on cdsh.scooter_id = cts.scooter_id and cts.dr = 0
   where cdsh.status = 2
   and cdsh.dr=0
   and cdsh.tenant_id=#{tenantId}
   and cdsh.driver_id=#{id}

   and cdsh.begin_time between #{assignBeginTime} and #{assignEndTime}


   and cdsh.end_time between #{removeBeginTime} and #{removeEndTime}



   and cts.license_plate like concat('%',#{keyword},'%')

select cdsh.driver_id as id, cts.license_plate as licensePlate, cdsh.begin_time as assignTime, cdsh.end_time as removeTime, cdsh.mileage as mileage from cor_driver_scooter_history cdsh inner join cor_tenant_scooter cts on cdsh.scooter_id = cts.scooter_id and cts.dr = 0 where cdsh.status = 2 and cdsh.dr=0 and cdsh.tenant_id=#{tenantId} and cdsh.driver_id=#{id}
and cdsh.begin_time between #{assignBeginTime} and #{assignEndTime}
and cdsh.end_time between #{removeBeginTime} and #{removeEndTime}
and cts.license_plate like concat('%',#{keyword},'%')
order by assignTime desc limit #{startRow},#{pageSize}

select DATE_FORMAT(cdy.updated_time, '%Y-%m-%d %H') as times, ifnull(sum(case when cdy.status = '3' then 1 end), 0) as rejected, ifnull(sum(case when cdy.status = '5' then 1 end), 0) as timeoutComplete, ifnull(sum(case when cdy.status = '6' then 1 end), 0) as completed, ifnull(sum(case when cdy.status = '7' then 1 end), 0) as cancel, ifnull(sum(case when cdy.status = '3' then 1 when cdy.status = '5' then 1 when cdy.status = '6' then 1 when cdy.status = '7' then 1 end), 0) as total from cor_delivery cdy where cdy.dr = 0 and cdy.tenant_id=#{tenantId} and cdy.deliverer_id=#{id} and DATE_FORMAT(cdy.updated_time, '%Y-%m-%d') = DATE_FORMAT(#{dateTime}, '%Y-%m-%d')
and cdy.status=#{status}
group by times

select DATE_FORMAT(cdy.updated_time, '%Y-%m-%d') as times, ifnull(sum(case when cdy.status = '3' then 1 end), 0) as rejected, ifnull(sum(case when cdy.status = '5' then 1 end), 0) as timeoutComplete, ifnull(sum(case when cdy.status = '6' then 1 end), 0) as completed, ifnull(sum(case when cdy.status = '7' then 1 end), 0) as cancel, ifnull(sum(case when cdy.status = '3' then 1 when cdy.status = '5' then 1 when cdy.status = '6' then 1 when cdy.status = '7' then 1 end), 0) as total from cor_delivery cdy where cdy.dr = 0 and cdy.deliverer_id=#{id} and cdy.tenant_id=#{tenantId} and (DATE_FORMAT(cdy.updated_time, '%Y-%m-%d') between DATE_FORMAT(#{startDateTime}, '%Y-%m-%d') and DATE_FORMAT(#{endDateTime}, '%Y-%m-%d'))
and cdy.status=#{status}
group by times

select DATE_FORMAT(cdy.updated_time,'%Y-%m-%d') as times, ifnull(sum(case when cdy.status = '3' then 1 end), 0) as rejected, ifnull(sum(case when cdy.status = '5' then 1 end), 0) as timeoutComplete, ifnull(sum(case when cdy.status = '6' then 1 end), 0) as completed, ifnull(sum(case when cdy.status = '7' then 1 end), 0) as cancel, ifnull(sum(case when cdy.status = '3' then 1 when cdy.status = '5' then 1 when cdy.status = '6' then 1 when cdy.status = '7' then 1 end), 0) as total from cor_delivery cdy where cdy.dr = 0 and cdy.tenant_id=#{tenantId} and cdy.deliverer_id=#{id} and (DATE_FORMAT(cdy.updated_time,'%Y-%m-%d') between DATE_FORMAT(#{startDateTime}, '%Y-%m-%d') and DATE_FORMAT(#{endDateTime}, '%Y-%m-%d'))
and cdy.status=#{status}
group by times

select DATE_FORMAT(cdy.updated_time, '%Y-%m') as times, ifnull(sum(case when cdy.status = '3' then 1 end), 0) as rejected, ifnull(sum(case when cdy.status = '5' then 1 end), 0) as timeoutComplete, ifnull(sum(case when cdy.status = '6' then 1 end), 0) as completed, ifnull(sum(case when cdy.status = '7' then 1 end), 0) as cancel, ifnull(sum(case when cdy.status = '3' then 1 when cdy.status = '5' then 1 when cdy.status = '6' then 1 when cdy.status = '7' then 1 end), 0) as total from cor_delivery cdy where cdy.dr = 0 and cdy.tenant_id=#{tenantId} and cdy.deliverer_id=#{id} and (DATE_FORMAT(cdy.updated_time, '%Y-%m') between DATE_FORMAT(#{startDateTime}, '%Y-%m') and DATE_FORMAT(#{endDateTime}, '%Y-%m'))
and cdy.status=#{status}
group by times
