mapper.select:
select
from ope_allocate allocate inner join ope_sys_user_profile profile on profile.sys_user_id = allocate.consignee_id and profile.dr=0
limit #{enter.startRow},#{enter.pageSize}

select stock.id as id, parts.parts_number as partsN, parts.en_name as enName, parts.cn_name as cnName, parts.parts_type as type, parts.supplier_id as supplierId, supplier.supplier_name as supplierName, parts.production_cycle as leadTime, stock.available_total as stocks from ope_stock stock inner join ope_parts parts on parts.id = stock.materiel_product_id and parts.dr = 0 inner join ope_supplier supplier on supplier.id = parts.supplier_id and supplier.dr = 0 where stock.dr = 0
and parts.parts_type =#{type}
and( parts.en_name like concat ("%",#{keyword},"%") or parts.cn_name like concat ("%",#{keyword},"%") or parts.parts_number like concat ("%",#{keyword},"%") )
