sql.where:
and ass.dr=0
and ass.status in

    #{item}


    and ass.status=#{enter.status}


    and ass.factory_id=#{enter.factoryId}


    and ass.payment_type =#{enter.paymentType}


    and (ass.created_time between#{enter.createdStartTime} and #{enter.createdEndTime} )


    and (
    ass.assembly_number like concat ("%",#{enter.keyword},"%")
    )

mapper.sql:
where oao.id =#{id}
and assBqc.dr = 0

    and (
    product.en_name like concat("%",#{keyword},"%") or
    and product.product_number like concat("%",#{keyword},"%")
    )

mapper.select:
select
from ope_assembly_order ass left join ope_factory factory on factory.id = ass.factory_id and factory.dr = 0 left join ope_sys_user_profile profile on profile.sys_user_id = ass.consignee_id and profile.dr = 0
order by ass.created_time desc limit #{enter.startRow},#{enter.pageSize}

select parts.id as partId, ifnull(sheet.price,0) as price, sheet.currency_unit as unit, supplier_id as supplierId from ope_parts parts inner join ope_supplier supplier on parts.supplier_id = supplier.id and supplier.dr = 0 left join ope_price_sheet sheet on parts.id = sheet.parts_id and sheet.currency_unit = 1 and sheet.dr = 0 where parts.dr=0
and parts.id in
#{item}

select
from ope_assembly_order ass left join ope_factory factory on factory.id = ass.factory_id and factory.dr = 0 left join ope_sys_user_profile profile on profile.sys_user_id = ass.consignee_id and profile.dr = 0
order by ass.created_time desc limit #{enter.startRow},#{enter.pageSize}

select item.id as id, item.serial_num as serialN, item.qc_result as qcStatus, item.created_time as qcDate, item.assembly_b_qc_id as assemblyBQcId from ope_assembly_qc_item item left join ope_assembly_b_qc bqc on bqc.id = item.assembly_b_qc_id and bqc.dr = 0 where bqc.id = #{id} and item.dr = 0
and item.qc_result=#{status}
and (item.created_time between #{qcStartTime} and #{qcEndTime} )

select item.id as id, item.serial_num as serialN, item.qc_result as qcStatus, item.created_time as qcDate, item.assembly_b_qc_id as assemblyBQcId from ope_assembly_qc_item item left join ope_assembly_b_qc bqc on bqc.id = item.assembly_b_qc_id and bqc.dr = 0 where item.dr = 0 and bqc.id in
#{item}
and item.qc_result=#{enter.status}
and (item.created_time between #{enter.qcStartTime} and #{enter.qcEndTime} )
