mapper.select:
select count(*) from ope_customer where dr = 0
and customer_type =#{customerType}
and industry_type =#{industryType}
and ( customer_first_name like concat("%",#{keyword},"%") or customer_last_name like concat("%",#{keyword},"%") or customer_full_name like concat("%",#{keyword},"%") or contact_first_name like concat("%",#{keyword},"%") or contact_last_name like concat("%",#{keyword},"%") or contact_full_name like concat("%",#{keyword},"%") or company_name like concat("%",#{keyword},"%") or email like concat("%",#{keyword},"%") )

select id as id, tenant_id as tenantId, customer_first_name as customerFirstName, customer_last_name as customerLastName, customer_full_name as customerFullName, contact_first_name as contactFirstName, contact_last_name as contactLastName, contact_full_name as contactFullName, company_name as companyName, email as email, customer_type as customerType, industry_type as industryType from ope_customer where dr = 0
and customer_type =#{customerType}
and industry_type =#{industryType}
and ( customer_first_name like concat("%",#{keyword},"%") or customer_last_name like concat("%",#{keyword},"%") or customer_full_name like concat("%",#{keyword},"%") or contact_first_name like concat("%",#{keyword},"%") or contact_last_name like concat("%",#{keyword},"%") or contact_full_name like concat("%",#{keyword},"%") or company_name like concat("%",#{keyword},"%") or email like concat("%",#{keyword},"%") ) order by created_time desc

select

from ope_customer opc
where 1=1
and opc.dr =0

    and opc.status =#{status}


    and opc.city =#{oneCityiD}

        and opc.distrust =#{twoCityiD}



    and opc.customer_type =#{customerType}


    and opc.industry_type=#{customerIndustry}


    and opc.customer_source =#{customerSource}



        and (opc.updated_time between #{createStartDateTime} and #{createEndDateTime} )


        and (opc.created_time between #{createStartDateTime} and #{createEndDateTime} )



    and (
       opc.customer_full_name like concat("%",#{keyword},"%")
    or opc.email like concat("%",#{keyword},"%")
    or opc.contact_full_name like concat("%",#{keyword},"%")
    )


    order by opc.updated_time desc


    order by opc.created_time desc

limit #{startRow},#{pageSize}

select count(*)
from ope_customer opc
where 1=1
and opc.dr =0

    and opc.status =#{status}


    and opc.city =#{oneCityiD}

        and opc.distrust =#{twoCityiD}



    and opc.customer_type =#{customerType}


    and opc.industry_type=#{customerIndustry}


    and opc.customer_source =#{customerSource}



        and (opc.updated_time between #{createStartDateTime} and #{createEndDateTime} )


        and (opc.created_time between #{createStartDateTime} and #{createEndDateTime} )



    and (
       opc.customer_full_name like concat("%",#{keyword},"%")
    or opc.email like concat("%",#{keyword},"%")
    or opc.contact_full_name like concat("%",#{keyword},"%")
    )


    order by opc.updated_time desc


    order by opc.created_time desc
