mapper.select:
select staff.id, profile.first_name as fitstName, profile.last_name as lastName, profile.country_code as countryCode, profile.tel_number as telphone from ope_sys_staff staff inner join ope_sys_user_profile profile on staff.sys_user_id = profile.sys_user_id and profile.dr = 0 left join ope_sys_user user on user.org_staff_id = staff.id and user.dr = 0 left join ope_sys_dept dept on dept.id = user.dept_id and dept.dr = 0 where staff.dr = 0
and dept.id in
#{item}

select dept.id as id , dept.name as name , dept.sort as sort , dept.p_id as pid , dept.dept_status as deptStatus from ope_sys_dept dept where dept.dr = 0
and( dept.name like concat ('%',#{keyWrod},'%') )
and dept.dept_status =#{deptType}
