sql.where:
and ofa.dr = 0
and ofa.tenant_id = #{tenantId}

    and ofa.status =#{status}


    and (ofa.factory_name like concat("%",#{keyword},"%")
    or ofa.factory_tag like concat("%",#{keyword},"%"))

mapper.select:
select
from ope_factory ofa
order by ofa.updated_time desc limit #{startRow},#{pageSize}
