mapper.sql:
where invorder.dr=0

    and invorder.invoice_status =#{invoiceStatus}


    and invorder.invoice_type=#{classType}


    and (
    invorder.invoice_no like concat('%',#{keyword},'%') or
    staff.first_name like concat('%',#{keyword},'%') or
    staff.last_name like concat('%',#{keyword},'%')
    )

order by invorder.created_time asc
