WHERE
pos.dr=0

    and  pos.dept_id=#{enter.deptId}


    and  pos.position_status=#{enter.positionStatus}


    and(
    pos.position_name like concat ('%',#{enter.keyWord},'%')
    )




            and pos.dept_id in

                #{item}



            and pos.created_by = #{enter.userId}
