mapper.select:
select count(1) from ope_combin_order combin left join ope_sys_staff staff on staff.id = combin.created_by and staff.dr = 0 where combin.dr=0 and combin.combin_type=#{classType}
and combin.combin_status =#{status}
and combin.combin_no like concat('%',#{keyword},'%')

select combin.id as id, combin.combin_type as combinType, combin.combin_no as combinNo, combin.combin_status as combinStatus, combin.combin_qty as combinQty, combin.combin_start_date as combinStartDate, combin.combin_end_date as combinEndDate, combin.principal_id as principalId, combin.principal_name as principalName, combin.country_code as countryCode, combin.telephone as telephone, combin.created_by as createById, staff.first_name as createByFistName, staff.last_name as createByLastName, combin.created_time as createdTime from ope_combin_order combin left join ope_sys_staff staff on staff.id = combin.created_by and staff.dr = 0 where combin.dr=0 and combin.combin_type=#{classType}
and combin.combin_status =#{status}
and combin.combin_no like concat('%',#{keyword},'%')
order by combin.created_time desc limit #{startRow},#{pageSize}

select part.id as id, part.parts_no as partN, part.en_name as partName, part.parts_type as partType, 100 as price, 1 as unit from ope_production_parts part where part.dr = 0
and part.id in
#{item}
