mapper.sql:
where purOrder.dr = 0

    and purOrder.purchase_status =#{purchaseStatus}


    and (
    supplier.supplier_name like concat ('%',#{keyword},'%') or
    purOrder.purchase_no like concat ('%',#{keyword},'%')
    )

mapper.select:
select part.id as partId, part.parts_no as partN, part.en_name as partName, part.parts_type as productType, 100 as price, 1 as unit, part.supplier_id as supplierId from ope_production_parts part where part.dr=0
and part.id in
#{item}

select count(1) from ope_production_parts part inner join ope_product_price price on part.id=price.product_id and price.dr=0 inner join ope_supplier supplier on supplier.id=part.supplier_id and supplier.dr=0 where part.disable=0 and part.dr=0 and part.qc_flag=1
and part.parts_type=#{productType}
and ( part.parts_no like concat ('%',#{keyword},'%') or part.en_name like concat ('%',#{keyword},'%') )

select part.id as id, part.parts_no as partsN, part.en_name as enName, part.parts_type as productType, 100 as price, 1 as unit, part.supplier_id as supplierId from ope_production_parts part inner join ope_product_price price on part.id=price.product_id and price.dr=0 inner join ope_supplier supplier on supplier.id=part.supplier_id and supplier.dr=0 where part.disable = 0 and part.dr = 0 and part.qc_flag=1
and part.parts_type=#{productType}
and ( part.parts_no like concat ('%',#{keyword},'%') or part.en_name like concat ('%',#{keyword},'%') )
limit #{startRow},#{pageSize}
