mapper.sql:
where purchas.dr=0
and purchas.status in

    #{item}


    and purchas.status =#{enter.status}


    and purchas.factory_id =#{enter.factoryId}


    and purchas.payment_type=#{param1.paymentType}


    and (purchas.created_time between #{enter.createdStartTime} and #{enter.createdEndTime})


    and (
    purchas.contract_no like concat ("%",#{enter.keyword},"%")
    )

order by purchas.created_time desc

mapper.select:
select product.id as id, product.parts_no as partsN, product.en_name as enName, product.cn_name as cnName, product.parts_type as productType, supplier.id as supplierId, supplier.supplier_name as supplierName, product.procurement_cycle as leadTime, sheet.price as price, sheet.standard_currency as unit from ope_production_parts product inner join ope_supplier supplier on supplier.id = product.supplier_id and supplier.dr = 0 inner join ope_product_price sheet on sheet.product_id = product.id and product_price_type = 1 and sheet.dr = 0
and supplier.id=#{enter.supplierId}
and product.parts_type in
#{item}
and ( product.parts_no like concat("%",#{enter.keyword},"%") or product.en_name like concat("%",#{enter.keyword},"%") or product.cn_name like concat("%",#{enter.keyword},"%") )

select
from ope_purchas purchas left join ope_sys_user_profile profile on purchas.consignee_id = profile.sys_user_id and profile.dr=0 left join ope_factory factory on factory.id = purchas.factory_id and factory.dr=0
limit #{enter.startRow},#{enter.pageSize}

select parts.id as id, parts.id as partId, parts.parts_no as partsN, parts.cn_name as cnName, parts.en_name as enName, parts.parts_type as productType, purchasb.supplier_id as supplierId, supplier.supplier_name as supplierName, parts.procurement_cycle as leadTime, sheet.price as price, sheet.standard_currency as unit, purchasb.total_count as qty, purchasb.total_count * sheet.price as subtotal from ope_purchas_b purchasb inner join ope_production_parts parts on purchasb.part_id = parts.id and parts.dr = 0 inner join ope_supplier supplier on purchasb.supplier_id = supplier.id and supplier.dr = 0 left join ope_product_price sheet on sheet.product_id = parts.id and sheet.dr = 0 and sheet.product_price_type = 1 where purchasb.dr = 0 and purchasb.purchas_id =#{id}
and parts.parts_type =#{type}
and purchasb.supplier_id=#{supplierId}
and ( parts.cn_name like concat ("%",#{keyword},"%") or parts.en_name like concat ("%",#{keyword},"%") or parts.parts_no like concat ("%",#{keyword},"%") )

select parts.id as partId, ifnull(sheet.price, 0) as price, sheet.standard_currency as unit, supplier_id as supplierId from ope_production_parts parts inner join ope_supplier supplier on parts.supplier_id = supplier.id and supplier.dr = 0 inner join ope_product_price sheet on parts.id = sheet.product_id and sheet.product_price_type = 1 and sheet.dr = 0 where parts.dr = 0
and parts.id in
#{item}

select productb.production_id as id, productb.parts_id as partId, parts.parts_no as partsN, parts.en_name as enName, parts.cn_name as cnName, parts.parts_type as productType, parts.supplier_id as supplierId, parts.supplier_id as supplierId, supplier.supplier_name as supplierName, parts.procurement_cycle as leadTime, ifnull(sheet.price, 0) * productb.parts_qty as price, sheet.standard_currency as unit, productb.parts_qty as qty from ope_production_parts_relation productb inner join ope_production_parts parts on parts.id = productb.parts_id and parts.dr = 0 inner join ope_supplier supplier on parts.supplier_id = supplier.id and supplier.dr = 0 inner join ope_product_price sheet on sheet.product_id = productb.parts_id and sheet.dr = 0 and sheet.product_price_type = 1 where productb.dr = 0
and productb.production_type=2 and productb.production_id in
#{item}

select purchasb.id as id, parts.parts_no as partsN, parts.en_name as enName, parts.cn_name as cnName, parts.parts_type as type, purchasb.total_count as totalQty from ope_purchas_b purchasb inner join ope_production_parts parts on purchasb.part_id = parts.id and purchasb.dr = 0 where purchasb.dr = 0 and purchasb.purchas_id = #{id}
and parts.parts_type =#{type}
and( parts.parts_no like concat ("%",#{keyword},"%") or parts.en_name like concat ("%",#{keyword},"%") or parts.cn_name like concat ("%",#{keyword},"%") )
