mapper.sql:
where purchas.dr=0
and purchas.status in

    #{item}


    and purchas.status =#{enter.status}


    and purchas.factory_id =#{enter.factoryId}


    and purchas.payment_type=#{param1.paymentType}


    and (purchas.created_time betwween #{enter.createdStartTime} and #{enter.createdEndTime})


    and (
    purchas.contract_no like concat ("%",#{enter.keyword},"%")
    )

order by purchas.created_time desc

mapper.select:
select part.id as id, product.product_number as partN, product.en_name as enName, product.cn_name as cnName, part.parts_type as type, supplier.id as supplierId, supplier.supplier_name as supplierName, part.production_cycle as leadTime, sheet.sales_price as price, sheet.currency_unit as unit from ope_parts_product product left join ope_parts_product_b productb on product.id = productb.parts_product_id and productb.dr = 0 and product.product_type in
#{item}
inner join ope_parts part on part.id = productb.parts_id and part.dr = 0 inner join ope_supplier supplier on supplier.id = part.supplier_id and supplier.dr = 0 inner join ope_regional_price_sheet sheet on sheet.parts_product_id = product.id and sheet.currency_unit = 1
and supplier.id=#{enter.supplierId}
and product.product_type =#{enter.type}
and ( product.product_number like concat("%",#{enter.keyword},"%") or product.en_name like concat("%",#{enter.keyword},"%") or product.cn_name like concat("%",#{enter.keyword},"%") )

select
from ope_purchas purchas left join ope_sys_user_profile profile on purchas.consignee_id = profile.id and profile.dr=0 left join ope_factory factory on factory.id = purchas.factory_id and factory.dr=0
limit #{enter.startRow},#{enter.pageSize}
