sql.where:
and opi.dr =0
and opi.status = #{status}
and opi.status in ('1','3','4','5')
and opp.color = #{color}
and (opi.created_time between #{startCreatedTime} and #{endCreatedTime})
and ( opi.order_no like concat("%",#{keyword},"%") or opi.email like concat("%",#{keyword},"%") or oc.customer_full_name like concat("%",#{keyword},"%") or oc.customer_first_name like concat("%",#{keyword},"%") or oc.customer_last_name like concat("%",#{keyword},"%") or oc.email like concat("%",#{keyword},"%") or opp.en_name like concat("%",#{keyword},"%") or opp.fr_name like concat("%",#{keyword},"%") or opp.cn_name like concat("%",#{keyword},"%") )

mapper.select:
select
from ope_customer_inquiry opi left join ope_customer oc on opi.customer_id = oc.id and oc.dr = 0 left join ope_parts_product opp on opi.product_id = opp.id and opp.dr = 0
order by opi.created_time desc limit #{startRow},#{pageSize}
