sql.where:
and opi.dr =0
and opi.status = #{status}
and opp.color_name = #{color}
and (opi.created_time between #{startCreatedTime} and #{endCreatedTime})
and ( opi.order_no like concat("%",#{keyword},"%") or opi.email like concat("%",#{keyword},"%") or oc.customer_full_name like concat("%",#{keyword},"%") or oc.customer_first_name like concat("%",#{keyword},"%") or oc.customer_last_name like concat("%",#{keyword},"%") or oc.email like concat("%",#{keyword},"%") )

mapper.select:
select
from ope_customer_inquiry opi left join ope_customer oc on opi.customer_id = oc.id and oc.dr = 0 left join ope_sale_scooter scooter on scooter.id = opi.product_id and scooter.dr = 0 left join ope_color opp on scooter.color_id = opp.id and opp.dr = 0 left join ope_specificat_type spe on scooter.specificat_id = spe.id and spe.dr = 0
order by opi.created_time desc limit #{startRow},#{pageSize}
