sql.where:
and osu.dr = 0
and osu.tenant_id = #{tenantId}

    and osu.status =#{status}


    and (osu.supplier_name like concat("%",#{keyword},"%")
    or osu.contact_email like concat("%",#{keyword},"%"))

mapper.select:
select
from ope_supplier osu
order by osu.updated_time desc limit #{startRow},#{pageSize}
