mapper.select:
select role.id as id, role.role_name as roleName, role.role_code as roleCode, role.role_desc as description from ope_sys_role role inner join ope_sys_role_dept dept on role.id = dept.role_id where 1=1
and dept.dept_id=#{deptId}
and role.role_name like concat ("%",#{keyword},"%")

select dept.id as deptId, dept.name as deptName, count(role.id) as totalCount from ope_sys_dept dept left join ope_sys_role_dept drole on dept.id = drole.dept_id left join ope_sys_role role on role.id = drole.role_id where dept.level != 0
and dept.id=#{deptId}
group by deptId, deptName
