mapper.sql:
where stock.dr = 0 and parts.dr=0 and stock.available_total > 0 and materiel_product_type != 4
and materiel_product_type=#{productType}
and (parts.parts_number like concat('%', #{keyword}, '%') or parts.fr_name like concat('%', #{keyword}, '%') or parts.en_name like concat('%', #{keyword}, '%') or parts.cn_name like concat('%', #{keyword}, '%') )

where stock.dr = 0 and product.dr=0 and stock.available_total > 0 and materiel_product_type = 4
and materiel_product_type=#{productType}
and (product.product_number like concat('%', #{keyword}, '%') or product.fr_name like concat('%', #{keyword}, '%') or product.en_name like concat('%', #{keyword}, '%') or product.cn_name like concat('%', #{keyword}, '%') )

where orderB.dr=0
           and orderB.id=#{id}

               and orderB.source_type #{productType}

           and (
               product.en_name  like concat('',#{keyword},'') or
               product.fr_name  like concat('',#{keyword},'') or
               product.cn_name  like concat('',#{keyword},'') or
               product.product_number like concat('',#{keyword},'')
               )

where orderB.dr=0
            and orderB.id=#{id}

                and orderB.source_type=#{productType}

            and (
                parts.en_name  like concat('',#{keyword},'') or
                parts.fr_name  like concat('',#{keyword},'') or
                parts.cn_name  like concat('',#{keyword},'') or
                parts.parts_number like concat('',#{keyword},'')
                )

where outOrder.dr=0

        and outOrder.status=#{status}


        and outOrder.consign_type=#{consignType}


        and (outOrder.created_time between #{createStartTime} and #{createEndTime})


        and (
            outOrder.order_no like concat ('%',#{keyword},'%') or
            outOrder.consign_company like concat ('%',#{keyword},'%') or
            outOrder.tracking_num like concat ('%',#{keyword},'%')
        )

mapper.select:
select stock.id, parts.parts_number as productN, parts.cn_name as cnName, parts.fr_name as frName, parts.en_name as enName, stock.available_total as stocks, stock.materiel_product_type as productType from ope_stock stock inner join ope_parts as parts on stock.materiel_product_id = parts.id
union select stock.id, product.product_number as productN, product.en_name as cnName, product.fr_name as frName, product.en_name as enName, stock.available_total as stocks, stock.materiel_product_type as type from ope_stock stock inner join ope_parts_product as product on stock.materiel_product_id = product.id
limit #{startRow},#{pageSize}

select orderB.id as id, product.product_number as productN, product.product_type as type, product.en_name as enName, product.fr_name as frName, product.cn_name as cnName, orderB.total_count as qty from ope_outwh_order_b orderB left join ope_parts_product product on orderB.part_product_id = product.id and orderB.source_type = 4 and product.dr=0
union select orderB.id as id, parts.parts_number as productN, parts.parts_type as type, parts.en_name as enName, parts.fr_name as frName, parts.cn_name as cnName, orderB.total_count as qty from ope_outwh_order_b orderB left join ope_parts parts on parts.id = orderB.part_product_id and orderB.source_type != 4n and parts.dr=0
limit #{startRow},#{pageSize}
