mapper.select:
SELECT sum(aa.count) from ( select count(*) AS count FROM ope_stock stock JOIN ope_parts_product product ON stock.materiel_product_id = product.id AND product.dr = 0 JOIN ope_regional_price_sheet sheet ON product.id = sheet.parts_product_id AND sheet.dr = 0
UNION SELECT count(*) AS count FROM ope_stock stock JOIN ope_parts parts ON stock.materiel_product_id = parts.id AND parts.dr = 0 JOIN ope_price_sheet sheet ON parts.id = sheet.parts_id AND sheet.dr = 0
) aa

SELECT stock.id AS id, product.cn_name AS cnName, product.en_name AS enName, product.product_number AS productNumber, stock.available_total AS intTotal, stock.materiel_product_type AS productType, sheet.sales_price AS price FROM ope_stock stock JOIN ope_parts_product product ON stock.materiel_product_id = product.id AND product.dr = 0 JOIN ope_regional_price_sheet sheet ON product.id = sheet.parts_product_id AND sheet.dr = 0
UNION SELECT stock.id AS id, parts.cn_name AS cnName, parts.en_name AS enName, parts.parts_number AS productNumber, stock.available_total AS intTotal, stock.materiel_product_type AS productType, sheet.price AS price FROM ope_stock stock JOIN ope_parts parts ON stock.materiel_product_id = parts.id AND parts.dr = 0 JOIN ope_price_sheet sheet ON parts.id = sheet.parts_id AND sheet.dr = 0
limit #{enter.startRow},#{enter.pageSize}

SELECT allocate.id as id, allocate.allocate_num as allocateNumber, allocate.count as qty, pro.id as consigneeId, pro.first_name as consigneeFristName, pro.last_name as consigneelastName, '2' as documentType, allocate.updated_time as inWhTime FROM ope_allocate allocate JOIN ope_sys_user_profile pro ON allocate.consignee_id = pro.id and pro.dr = 0
UNION SELECT ord.id as id, ord.assembly_number as allocateNumber, ord.in_wait_wh_total as qty, pro.id as consigneeId, pro.first_name as consigneeFristName, pro.last_name as consigneelastName, '3' as documentType, ord.updated_time as inWhTime FROM ope_assembly_order ord JOIN ope_sys_user_profile pro ON ord.consignee_id = pro.id and pro.dr = 0
limit #{enter.startRow},#{enter.pageSize}

