mapper.select:
SELECT allocate.id as id, allocate.allocate_num as allocateNumber, allocate.count-allocate.pending_storage_total as qty, allocate.count as sumTotal, pro.id as consigneeId, pro.first_name as consigneeFristName, pro.last_name as consigneelastName, '2' as documentType, allocate.created_time as inWhTime FROM ope_allocate allocate JOIN ope_sys_user_profile pro ON allocate.consignee_id = pro.id and pro.dr = 0
UNION SELECT ord.id as id, ord.assembly_number as allocateNumber, ord.total_qty-ord.in_wait_wh_total as qty, ord.total_qty as sumTotal, pro.id as consigneeId, pro.first_name as consigneeFristName, pro.last_name as consigneelastName, '3' as documentType, ord.created_time as inWhTime FROM ope_assembly_order ord JOIN ope_sys_user_profile pro ON ord.consignee_id = pro.id and pro.dr = 0
limit #{enter.startRow},#{enter.pageSize}

SELECT sum(aa.count) from ( select count(*) AS count FROM ope_stock stock JOIN ope_parts_product product ON stock.materiel_product_id = product.id AND product.dr = 0 JOIN ope_regional_price_sheet sheet ON product.id = sheet.parts_product_id AND sheet.dr = 0 WHERE stock.dr = 0 AND stock.available_total > 0 AND stock.whse_id IN
#{whseItem.id}
AND stock.materiel_product_type in
#{typeItem}
AND sheet.currency_unit = #{amountType} UNION SELECT count(*) AS count FROM ope_stock stock JOIN ope_parts parts ON stock.materiel_product_id = parts.id AND parts.dr = 0 JOIN ope_price_sheet sheet ON parts.id = sheet.parts_id AND sheet.dr = 0 WHERE stock.dr = 0 AND stock.available_total > 0 AND stock.whse_id IN
#{whseItem.id}
AND stock.materiel_product_type in
#{typeItem}
AND sheet.currency_unit = #{amountType} ) aa

select sum(storedStock.total) from ( SELECT count(*) as total FROM ope_stock stock inner JOIN ope_parts_product product ON stock.materiel_product_id = product.id and product.dr = 0 inner JOIN ope_regional_price_sheet sheet ON product.id = sheet.parts_product_id and sheet.dr = 0 AND sheet.currency_unit = #{amountType}
union SELECT count(*) as total FROM ope_stock stock inner JOIN ope_parts part ON stock.materiel_product_id = part.id and part.dr = 0 inner JOIN ope_price_sheet sheet ON part.id = sheet.parts_id and sheet.dr = 0
) storedStock

SELECT stock.id as id, product.cn_name as cnName, product.en_name as enName, product.product_number as productNumber, stock.wait_stored_total as intTotal, stock.materiel_product_type as productType, sheet.sales_price * stock.wait_stored_total as price FROM ope_stock stock inner JOIN ope_parts_product product ON stock.materiel_product_id = product.id and product.dr = 0 inner JOIN ope_regional_price_sheet sheet ON product.id = sheet.parts_product_id and sheet.dr = 0 AND sheet.currency_unit = #{amountType}
union SELECT stock.id as id, part.cn_name as cnName, part.en_name as enName, part.parts_number as productNumber, stock.wait_stored_total as intTotal, stock.materiel_product_type as productType, sheet.price * stock.wait_stored_total as price FROM ope_stock stock inner JOIN ope_parts part ON stock.materiel_product_id = part.id and part.dr = 0 inner JOIN ope_price_sheet sheet ON part.id = sheet.parts_id and sheet.dr = 0
limit #{enter.startRow},#{enter.pageSize}

SELECT count(*) FROM ope_stock stock JOIN ope_parts_product product ON stock.materiel_product_id = product.id and product.dr = 0 JOIN ope_regional_price_sheet sheet ON product.id = sheet.parts_product_id and sheet.dr = 0 WHERE stock.dr=0 AND stock.wait_stored_total>0 AND stock.whse_id = #{whseid} AND stock.materiel_product_type in
#{typeItem}
AND sheet.currency_unit = #{amountType}

SELECT count(*) FROM ope_stock stock JOIN ope_parts parts ON stock.materiel_product_id = parts.id and parts.dr = 0 JOIN ope_price_sheet sheet ON parts.id = sheet.parts_id and sheet.dr = 0 WHERE stock.dr=0 AND stock.wait_product_total>0 AND stock.whse_id=#{whseid} AND stock.materiel_product_type in
#{typeItem}
AND sheet.currency_unit = #{amountType}

select sum(outWh.total) from( select count(*) as total from ope_fr_stock stock inner JOIN ope_parts_product part ON stock.materiel_product_id = part.id and part.dr = 0 and stock.materiel_product_type = 4 LEFT JOIN ope_regional_price_sheet sheet ON sheet.parts_product_id = part.id and sheet.dr = 0 and sheet.currency_unit = #{amountType}
UNION select count(*) as total from ope_fr_stock stock inner JOIN ope_parts part ON stock.materiel_product_id = part.id and part.dr = 0 and stock.materiel_product_type != 4 LEFT JOIN ope_price_sheet sheet on sheet.parts_id = part.id and sheet.dr = 0
)outWh

select stock.id as id, stock.materiel_product_id AS partId, stock.materiel_product_type AS productType, part.cn_name AS cnName, part.en_name AS enName, part.product_number AS productNumber, stock.available_total AS intTotal, sheet.sales_price as price from ope_fr_stock stock inner JOIN ope_parts_product part ON stock.materiel_product_id = part.id and part.dr = 0 and stock.materiel_product_type = 4 LEFT JOIN ope_regional_price_sheet sheet ON sheet.parts_product_id = part.id and sheet.dr = 0 and sheet.currency_unit = #{amountType}
UNION select stock.id as id, stock.materiel_product_id AS partId, stock.materiel_product_type AS productType, part.cn_name AS cnName, part.en_name AS enName, part.product_number AS productNumber, stock.available_total AS intTotal, sheet.price as price from ope_fr_stock stock inner JOIN ope_parts_product part ON stock.materiel_product_id = part.id and part.dr = 0 and stock.materiel_product_type != 4 LEFT JOIN ope_price_sheet sheet on sheet.parts_id=part.id and sheet.dr = 0
limit #{enter.startRow},#{enter.pageSize}
