mapper.sql:
WHERE s.dr=0 AND s.whse_id IN (#{defaultParam.purchas},#{defaultParam.assembly}) AND s.materiel_product_type in (#{defaultParam.combination},#{defaultParam.scooter}) AND e.currency_unit = #{defaultParam.fr}
and s.materiel_product_type=#{enter.stockType}
and ( b.cn_name like concat ("%",#{enter.keyword},"%") or b.en_name like concat ("%",#{enter.keyword},"%") or b.product_number like concat ("%",#{enter.keyword},"%") )

WHERE s.dr=0 AND s.whse_id IN (#{defaultParam.purchas},#{defaultParam.assembly}) AND s.materiel_product_type in (#{defaultParam.parts},#{defaultParam.battery},#{defaultParam.accessory}) AND e.currency_unit = #{defaultParam.fr}
and s.materiel_product_type=#{enter.stockType}
and ( p.cn_name like concat ("%",#{enter.keyword},"%") or p.en_name like concat ("%",#{enter.keyword},"%") or p.parts_number like concat ("%",#{enter.keyword},"%") )

mapper.select:
SELECT sum(aa.count) from ( select count(*) AS count FROM ope_stock s JOIN ope_parts_product b ON s.materiel_product_id = b.id and b.dr = 0 JOIN ope_regional_price_sheet e ON b.id = e.parts_product_id and e.dr = 0
UNION SELECT count(*) AS count FROM ope_stock s JOIN ope_parts p ON s.materiel_product_id = p.id and p.dr = 0 JOIN ope_price_sheet e ON s.id = e.parts_id and e.dr = 0
) aa

SELECT * from ((SELECT s.id as id, b.cn_name as cnName, b.en_name as enName, b.product_number as productNumber, s.int_total as intTotal, s.materiel_product_type as productType, e.sales_price as price FROM ope_stock s JOIN ope_parts_product b ON s.materiel_product_id = b.id and b.dr = 0 JOIN ope_regional_price_sheet e ON b.id = e.parts_product_id and e.dr = 0
order by s.updated_time desc) UNION (SELECT s.id as id, p.cn_name as cnName, p.en_name as enName, p.parts_number as productNumber, s.int_total as intTotal, s.materiel_product_type as productType, e.price as price FROM ope_stock s JOIN ope_parts p ON s.materiel_product_id = p.id and p.dr = 0 JOIN ope_price_sheet e ON s.id = e.parts_id and e.dr = 0
order by s.updated_time desc)) bb limit #{enter.startRow},#{enter.pageSize}

mapper.sql:
WHERE e.dr=0 and e.`status` = 4
and e.updated_time between #{enter.startTime} and #{enter.endTime}
and ( e.allocate_num like concat ("%",#{enter.keyword},"%") or e.consignee_id like concat ("%",#{enter.keyword},"%") )

WHERE r.dr=0 and r.`status` = 3
and e.updated_time between #{enter.startTime} and #{enter.endTime}
and ( r.assembly_number like concat ("%",#{enter.keyword},"%") or r.consignee_id like concat ("%",#{enter.keyword},"%") )

mapper.select:
SELECT * from ((SELECT e.id as id, e.allocate_num as allocateNumber, e.count as qty, s.full_name as consignee, '2' as productType, e.updated_time as inWhTime FROM ope_allocate e JOIN ope_sys_user_profile s ON e.consignee_id = s.id and s.dr = 0
order by e.updated_time desc) UNION (SELECT r.id as id, r.assembly_number as allocateNumber, r.in_wait_wh_total as qty, s.full_name as consignee, '3' as productType, r.updated_time as inWhTime FROM ope_assembly_order r JOIN ope_sys_user_profile s ON r.consignee_id = s.id and s.dr = 0
order by r.updated_time desc)) bb limit #{enter.startRow},#{enter.pageSize}

mapper.sql:
WHERE e.dr=0 and e.`status` = 3
and e.created_time between #{enter.startTime} and #{enter.endTime}
and ( e.allocate_num like concat ("%",#{enter.keyword},"%") or e.consignee_id like concat ("%",#{enter.keyword},"%") )

WHERE r.dr=0 and r.`status` = 5
and e.created_time between #{enter.startTime} and #{enter.endTime}
and ( r.assembly_number like concat ("%",#{enter.keyword},"%") or r.consignee_id like concat ("%",#{enter.keyword},"%") )

mapper.select:
SELECT * from ((SELECT e.id as id, e.allocate_num as allocateNumber, e.count-e.pending_storage_total as qty, e.count as sumTotal, s.full_name as consignee, '2' as productType, e.created_time as inWhTime FROM ope_allocate e JOIN ope_sys_user_profile s ON e.consignee_id = s.id and s.dr = 0
order by e.updated_time desc) UNION (SELECT r.id as id, r.assembly_number as allocateNumber, r.total_qty-r.in_wait_wh_total as qty, r.total_qty as sumTotal, s.full_name as consignee, '3' as productType, r.created_time as inWhTime FROM ope_assembly_order r JOIN ope_sys_user_profile s ON r.consignee_id = s.id and s.dr = 0
order by r.updated_time desc)) bb limit #{enter.startRow},#{enter.pageSize}
