mapper.select:
select scooter.id as id, scooter.picture as pictures, scooter.scooter_no as scooterNo, scooter.status as status, scooter.available_status as availableStatus, scooter.box_status as boxStatus, scooter.model as model, scooter.license_plate as licensePlate, scooter.license_plate_picture as licensePlatePicture, scooter.license_plate_time as licensePlateTime, scooter.scooter_id_picture as scooterIdPicture, scooter.insurance as insurance, scooter.insure_time as insureTime, scooter.revision as revision, status.scooter_ecu_id as scooterEcuId, status.longitule as longitule, status.latitude as latitude, status.battery as battery, ifnull(scooter.total_mileage, 0) as totalmileage from sco_scooter scooter inner join sco_scooter_status status on scooter.id = status.scooter_id where scooter.dr = 0
and scooter.id in
#{item}

select scooter.id as id, scooter.picture as pictures, scooter.scooter_no as scooterNo, scooter.status as status, scooter.available_status as availableStatus, scooter.box_status as boxStatus, scooter.model as model, scooter.license_plate as licensePlate, scooter.license_plate_picture as licensePlatePicture, scooter.license_plate_time as licensePlateTime, scooter.scooter_id_picture as scooterIdPicture, scooter.insurance as insurance, scooter.insure_time as insureTime, scooter.revision as revision, status.scooter_ecu_id as scooterEcuId, status.longitule as longitule, status.latitude as latitude, status.battery as battery, ifnull(scooter.total_mileage, 0) as totalmileage from sco_scooter scooter inner join sco_scooter_status status on scooter.id = status.scooter_id where scooter.dr = 0
and scooter.license_plate in
#{item}

select scooter.id as id, scooter.picture as pictures, scooter.scooter_no as scooterNo, scooter.status as status, scooter.available_status as availableStatus, scooter.box_status as boxStatus, scooter.model as model, scooter.license_plate as licensePlate, scooter.license_plate_picture as licensePlatePicture, scooter.license_plate_time as licensePlateTime, scooter.scooter_id_picture as scooterIdPicture, scooter.insurance as insurance, scooter.insure_time as insureTime, scooter.revision as revision, status.scooter_ecu_id as scooterEcuId, status.longitule as longitule, status.latitude as latitude, status.battery as battery, ifnull(scooter.total_mileage, 0) as totalmileage from sco_scooter scooter inner join sco_scooter_status status on scooter.id = status.scooter_id where scooter.dr = 0
and scooter.id=#{id} and scooter.scooter_no=#{scooterNo}
and scooter.scooter_no=#{scooterNo}
and scooter.id=#{id}
limit 1

mapper.update:
update sco_scooter
set `status` = #{status,jdbcType=VARCHAR}, updated_by = #{userId,jdbcType=BIGINT}, updated_time = now()
where id = #{id,jdbcType=BIGINT}

        update sco_scooter
	    set `status` = #{status}, total_mileage = #{totalMiles}, updated_time = #{date}
	    where tablet_sn = #{tabletSn}

        update sco_scooter
	    set model = #{scooterModel}, updated_time = #{updateTime}
	    where tablet_sn = #{tabletSn}

update sco_scooter
set license_plate = #{licensePlate}
where id = #{id}
