mapper.select:
select status, count(*) as totalCount
from cor_delivery delivery
where delivery.dr = 0
and delivery.tenant_id=#{tenantId}
and delivery.deliverer_id=#{userId}

    and delivery.status in

        #{item}


group by delivery.status

select delivery.*, (case when delivery.status=5 or delivery.status=6 or delivery.status=7 then null when TIMESTAMPDIFF(SECOND, NOW(), delivery.eta) >= 0 then null when TIMESTAMPDIFF(SECOND, NOW(), delivery.eta) < 0 then true end) as label from cor_delivery delivery where delivery.dr = 0 and delivery.deliverer_id=#{userId} and delivery.tenant_id=#{tenantId} and delivery.status in('1','2')
and delivery.status in
#{item}

select count(*)
from cor_delivery_trace
where dr=0
and status = #{status}
and user_id=#{userId}
